body.script:
const logEl = document.getElementById('log');
const btnProcessar = document.getElementById('btnProcessar');
const inputFiles = document.getElementById('xmlFiles');

function log(msg) {
  logEl.textContent += msg + "\n";
}

function limparLog() {
  logEl.textContent = "";
}

function getText(parent, tagName) {
  if (!parent) return "";
  const els = parent.getElementsByTagName(tagName);
  if (els.length === 0) return "";
  return (els[0].textContent || "").trim();
}

function adicionarAoResumo(mapResumo, codProd, nomeProd, unidade, qtd) {
  const chave = codProd + "|" + unidade;
  if (mapResumo.has(chave)) {
    const item = mapResumo.get(chave);
    item.quantidade += qtd;
  } else {
    mapResumo.set(chave, {
      codProd,
      nomeProd,
      unidade,
      quantidade: qtd
    });
  }
}

btnProcessar.addEventListener('click', async () => {
  limparLog();

  const files = inputFiles.files;
  if (!files || files.length === 0) {
    log("Nenhum arquivo XML selecionado.");
    return;
  }

  log(Processando ${files.length} arquivo(s)...);

  const relatorio = [];
  relatorio.push(["Nota", "Volumes", "Peso", "Destinatário", "Endereço", "Cidade"]);

  const resumoMap = new Map();

  let totalNotas = 0;
  let totalVolumes = 0;
  let totalPeso = 0;

  const parser = new DOMParser();

  for (const file of files) {
    try {
      const text = await file.text();
      const xmlDoc = parser.parseFromString(text, "application/xml");

      let infNFe = xmlDoc.getElementsByTagName("infNFe")[0];
      if (!infNFe) {
        log(Arquivo ${file.name}: infNFe não encontrado.);
        continue;
      }

      const ide = infNFe.getElementsByTagName("ide")[0];
      const numeroNota = getText(ide, "nNF");

      const transp = infNFe.getElementsByTagName("transp")[0];
      let volumes = 0;
      let peso = 0;

      if (transp) {
        const vol = transp.getElementsByTagName("vol")[0];
        if (vol) {
          volumes = parseFloat(getText(vol, "qVol").replace(",", ".")) || 0;
          peso = parseFloat(getText(vol, "pesoB").replace(",", ".")) || 0;
        }
      }

      const dest = infNFe.getElementsByTagName("dest")[0];
      const destinatario = dest ? getText(dest, "xNome") : "";
      let endereco = "";
      let cidade = "";

      if (dest) {
        const enderDest = dest.getElementsByTagName("enderDest")[0];
        if (enderDest) {
          endereco = ${getText(enderDest, "xLgr")}, ${getText(enderDest, "nro")};
          cidade = getText(enderDest, "xMun");
        }
      }

      relatorio.push([
        numeroNota,
        volumes,
        peso,
        destinatario,
        endereco,
        cidade
      ]);

      totalNotas++;
      totalVolumes += volumes;
      totalPeso += peso;

      const dets = infNFe.getElementsByTagName("det");
      for (let i = 0; i < dets.length; i++) {
        const prod = dets[i].getElementsByTagName("prod")[0];
        if (!prod) continue;

        const cod = getText(prod, "cProd");
        const nome = getText(prod, "xProd");
        const unidade = getText(prod, "uCom");
        const q = parseFloat(getText(prod, "qCom").replace(",", ".")) || 0;

        adicionarAoResumo(resumoMap, cod, nome, unidade, q);
      }

      log(OK: ${file.name});
    } catch (e) {
      log(Erro no arquivo ${file.name}: ${e});
    }
  }

  relatorio.push([]);
  relatorio.push(["TOTAL DE NOTAS:", totalNotas]);
  relatorio.push(["TOTAL DE VOLUMES:", totalVolumes]);
  relatorio.push(["TOTAL DE PESO:", totalPeso]);

  const resumoProdutos = [["Código", "Produto", "Unidade", "Quantidade"]];
  for (const [, item] of resumoMap) {
    resumoProdutos.push([
      item.codProd,
      item.nomeProd,
      item.unidade,
      item.quantidade
    ]);
  }

  const wb = XLSX.utils.book_new();
  XLSX.utils.book_append_sheet(wb, XLSX.utils.aoa_to_sheet(relatorio), "Relatorio");
  XLSX.utils.book_append_sheet(wb, XLSX.utils.aoa_to_sheet(resumoProdutos), "ResumoProduto");
  XLSX.writeFile(wb, "Relatorio_NFe.xlsx");

  log("\nArquivo XLSX gerado!");
});
